<?phpnamespace Modules\Academics\Controllers;use Core\Controllers\NodeController;class AcdStudentmarksDetails extends NodeController{    //put your code here    public function acdStudentmarksDetailsBeforeMRADataValidate()    {        $errors=array();        $requestedData=$this->_requestedData;                 $examinationMarksId=\Core::getValueFromArray($requestedData, "acd_studentmarks_details_parentidvalue");        $Data=\Core::getValueFromArray($requestedData, "acd_studentmarks_details_save");        $db= new \Core\DataBase\ProcessQuery();        $db->setTable("acd_examination_marks");        $db->addField("acd_examination_schedule_id");        $db->addWhere("id='".$examinationMarksId."'");        $acd_examination_schedule_id=$db->getValue();        $db= new \Core\DataBase\ProcessQuery();        $db->setTable("acd_examination_schedule_details");        $db->addWhere("acd_examination_schedule_id='".$acd_examination_schedule_id."'");        $examinationDetails=$db->getRows("cur_class_subject_id");        $db= new \Core\DataBase\ProcessQuery();        $db->setTable("acd_studentmarks_details");        $db->addWhere("acd_examination_marks_id='".$examinationMarksId."'");        $studentMarks=$db->getRows("id");                if(count($Data)>0)        {                       foreach ($Data as $key => $data)            {                if(\Core::getValueFromArray($data, "core_attendance_status_id")=='Present')                {                    $class_subject_id=$studentMarks[$key]['cur_class_subject_id'];                    $subjectDetails=$examinationDetails[$class_subject_id];                    if($subjectDetails['max_marks']<$data['obtained_marks'] || $data['obtained_marks']<$subjectDetails['min_marks'])                    {                        $errors['error_'.$this->_nodeName.'_'.$key.'_obtained_marks']=" Marks Must in Between ".$subjectDetails['min_marks'].",".$subjectDetails['max_marks']." only ";                    }                }                else                {                    if($data['obtained_marks']>0 || $data['obtained_marks']<0)                    {                        $errors['error_'.$this->_nodeName.'_'.$key.'_obtained_marks']=" Marks Must be zero ";                    }                }            }        }        return $errors;    }}