<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of DataInstall * * @author ramesh */namespace Modules\Academics\Data;class DataInstall{    //put your code here    function __construct()    {        $this->setDataUp();    }    protected function setDataUp()    {        $nodesArray=array(            "ModuleCreate"=>"ModuleCreate",            "AcdExamGroup"=>"AcdExamGroup",            "AcdExamName"=>"AcdExamName",            "AcdExaminationMarks"=>"AcdExaminationMarks",            "AcdExaminationSchedule"=>"AcdExaminationSchedule",            "AcdExaminationScheduleDetails"=>"AcdExaminationScheduleDetails",            "AcdFinalMarks"=>"AcdFinalMarks",            "AcdGradeMaster"=>"AcdGradeMaster",            "AcdGradeMethod"=>"AcdGradeMethod",            "AcdGradeMethodDetails"=>"AcdGradeMethodDetails",            "AcdGradeType"=>"AcdGradeType",            "AcdLinkExam"=>"AcdLinkExam",            "AcdModeofexam"=>"AcdModeofexam",            "AcdOverallGrade"=>"AcdOverallGrade",            "AcdStudentmarksDetails"=>"AcdStudentmarksDetails",            "AcdSubgroupexams"=>"AcdSubgroupexams",            "ListBranch"=>"ListBranch",            "AcdStudentAttendance"=>"AcdStudentAttendance",            "AcdStudentAttendanceDetails"=>"AcdStudentAttendanceDetails",            "Acdprogressreport"=>"Acdprogressreport",            );        foreach ($nodesArray as $node)        {            $nodeClass="\Modules\Academics\Data\'".$node;            $nodeClass=str_replace("'","",$nodeClass);            $rnode=new $nodeClass();            $rnode->execute();        }    }}