<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of CoreBackupType * * @author ramesh */namespace Modules\Librarymanagement\Setup;class BookDetails {    //put your code here    function execute() {        $cc = new \CoreClass();        $setup=$cc->getObject("\Core\DataBase\Setup");        $setup->setTable("lib_book_deatils");        if (!$setup->tableExists($setup->getTable())) {            $setup->setDisplayValue("Book Details");            $setup->addColumnName(array(                "name" => "id",                "displayValue" => "Book Id",                "primary" => 1,                "default" => NULL,                "type" => "int",                "size" => "15",                "auto_increment" => 1,            ));             $setup->addColumnName(array(                "name" => "lib_publisher_id",                "displayValue" => "Publisher Id",                "default" => NULL,                "type" => "varchar",                "size" => "255"            ));            $setup->addColumnName(array(                "name" => "name",                "displayValue" => "Name",                "default" => NULL,                "type" => "varchar",                "size" => "255"            ));            $setup->addColumnName(array(                "name" => "code",                "displayValue" => "Code",                "default" => NULL,                "type" => "varchar",                "size" => "255"            ));            $setup->addColumnName(array(                "name" => "edition",                "displayValue" => "Edition",                "default" => NULL,                "type" => "varchar",                "size" => "255"            ));            $setup->addColumnName(array(                "name" => "description",                "displayValue" => "Description",                "default" => NULL,                "type" => "text",            ));            $setup->addColumnName(array(                "name" => "image",                "displayValue" => "Image",                "default" => NULL,                "type" => "longtext",            ));            $setup->addColumnName(array(                "name" => "pages",                "displayValue" => "Pages",                "default" => NULL,                "type" => "int",                "size" => "11"            ));            $setup->addColumnName(array(                "name" => "createdby",                "displayValue" => "Created Id",                "default" => NULL,                "type" => "int",                "size" => "11"            ));            $setup->addColumnName(array(                "name" => "createdat",                "displayValue" => "Created Datetime",                "default" => NULL,                "type" => "datetime"            ));            $setup->addColumnName(array(                "name" => "updatedby",                "displayValue" => "Updated Id",                "default" => NULL,                "type" => "int",                "size" => "11"            ));            $setup->addColumnName(array(                "name" => "updatedat",                "displayValue" => "Updated Datetime",                "default" => NULL,                "type" => "datetime"            ));            $setup->create();        }    }}